import React, { Component } from "react";
import BookCard from "../BookCard";
import Row from "../Row";
import SearchForm from "../SearchForm";
import Jumbotron from "../Jumbotron";
import API from "../../utils/API";

class Search extends Component {

    state = {
        search: "",
        results: []
        };

    // on page load, 
    componentDidMount() {
        console.log("Mounted");
        this.searchBooks("Jim Harrison");
    }

    searchBooks = query => {
        API.search(query)
            .then(res => this.setState({results:res.data.items})).then(console.log(this.state.results))
            .catch(err => console.log(err));
    }

    // on Save click,
    handleSaveClick = event => {
    // Log book ID (which is the event)
        const bookInfo = event;
        console.log(bookInfo);
    // We want to save this book to the database
      API.saveBook(bookInfo)
        .then(res => console.log(res))
        .catch(err => console.log(err));
    }

    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]:value
        });
    }

    handleFormSubmit = event => {
        event.preventDefault();
        this.searchBooks(this.state.search);
    }

    render() {
        return (
            <div className="container-fluid">
                <Jumbotron />
                <Row>
                <SearchForm 
                    search={this.state.search}
                    handleFormSubmit={this.handleFormSubmit}
                    handleInputChange={this.handleInputChange}
                />
                </Row>
                <Row >
                    {this.state.results.map(book => ( 
                        <BookCard
                            id={book.id} 
                            key={book.id} 
                            title={book.volumeInfo.title} 
                            description={book.volumeInfo.description} 
                            image={book.volumeInfo.imageLinks.thumbnail} 
                            link={book.volumeInfo.infoLink} 
                            handleSaveClick={this.handleSaveClick}
                        />
                    ))}
                </Row>
            </div>
        )
    }
}

export default Search;
